import Head from "next/head";
import checkAuth from "../lib/checkAuth";
import axios from "axios";
export default class DashBoardIndex extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            sent: 0,
            points: 0,
            pointsToUse: 0,
            msgToSend: "",
            customEmailList: "",
            leads: 0,
            pageLoading: true,
            decisor: false,
            decisorLoading: false,
        }

        this.setDecisorOn = this.setDecisorOn.bind(this);
        this.setDecisorOff = this.setDecisorOff.bind(this);
        this.setPointsToUse = this.setPointsToUse.bind(this);
        this.setMsgToSend = this.setMsgToSend.bind(this);
        this.setCustomEmailList = this.setCustomEmailList.bind(this);
    }

    static async getInitialProps({ req, res }) {
        return checkAuth(req, res);
    }

    componentDidMount() {
        if (this.props.loggedIn && this.props.id != null) {
            axios({
                url: "/api/user",
                method: "POST",
                data: {
                    id: this.props.id
                }
            }).then(response => {
                this.setState({
                    sent: response.data.sent,
                    points: response.data.points,
                    leads: response.data.leads,
                    pageLoading: false
                });
            }).catch(err => {
                if (err) console.log(err);
            })
        }
    }


    setPointsToUse(elem) {
        this.setState({
            pointsToUse: elem.target.value
        })
    }

    setMsgToSend(elem) {
        this.setState({
            msgToSend: elem.target.value
        })
    }

    setCustomEmailList(elem) {
        this.setState({
            customEmailList: elem.target.value
        })
    }

    setDecisorOn() {
        this.setState({
            decisor: true,
        });
    }

    setDecisorOff() {
        this.setState({
            decisor: false,
        });
    }

    render() {

        function decisorDom(points, pointsToUse, setDecisorOff) {
            if (pointsToUse <= points) {
                return (
                    <div className="container decisor-items-container">
                        <div className="row">
                            <div className="col-md-12">
                                <h2>You will use <b>{pointsToUse}</b> points.<br></br><br></br>Points left: <b>{points - pointsToUse}</b><br></br><br></br>Are you sure?</h2>
                            </div>
                            <div className="col-md-12 row">
                                <div className="col-md-6">
                                    <button className="decisor-button">Yes</button>
                                </div>
                                <div className="col-md-6">
                                    <button className="decisor-button black">No</button>
                                </div>
                            </div>
                        </div>
                    </div>
                );
            } else {
                return (
                    <div className="container decisor-items-container">
                        <div className="row">
                            <div className="col-md-12">
                                <h2>You don't have enough points.<br></br><br></br>You need {pointsToUse - points == 1 ? pointsToUse - points + " point" : pointsToUse - points + " points"}.</h2>
                            </div>
                            <div className="col-md-12 row">
                                <div className="col-md-6">
                                    <button className="decisor-button">Add Points</button>
                                </div>
                                <div className="col-md-6">
                                    <button className="decisor-button black" onClick={setDecisorOff}>Close</button>
                                </div>
                            </div>
                        </div>
                    </div>
                )
            }
        }


        return (
            <React.Fragment>
                <Head>
                    <title>Dashboard</title>
                    <link href="/css/bootstrap.css" rel="stylesheet"></link>
                    <link href="/css/dashboard.css" rel="stylesheet"></link>
                </Head>
                <div className={this.state.pageLoading ? "loading" : "loading hide"}>
                        <div class="loadingio-spinner-eclipse-h5c55szghjc">
                            <div class="ldio-3kphjdhe4ws">
                            <div></div>
                            </div>
                            </div>
                </div>
                <div className={this.state.decisor ? "decisor active" : "decisor"}>
                    {decisorDom(this.state.points, this.state.pointsToUse, this.setDecisorOff)}
                </div>
                <div className="container-fluid">
                    <div className="row center">
                        <div className="col-md-4">
                            <div className="statistics-block">
                                <h2>E-Mails Sent</h2>
                                <h3>{this.state.sent}</h3>
                            </div>
                        </div>
                        <div className="col-md-4">
                            <div className="statistics-block">
                                <h2>Available Points</h2>
                                <h3>{this.state.points}</h3>
                            </div>
                        </div>
                        <div className="col-md-4">
                            <div className="statistics-block">
                                <h2>Leads</h2>
                                <h3>{this.state.leads}</h3>
                            </div>
                        </div>
                    </div>
                    <div className="row" style={{ margin: "5rem 0" }}>
                        <div className="col-md-4 input-block center">
                            <h3>Custom E-Mail List (Seperate by comma)</h3>
                            <textarea className="custom-list" type="text" placeholder="Leave blank if you don't want to add custom E-Mails" onChange={this.setCustomEmailList}></textarea>
                        </div>
                        <div className="col-md-8 input-block center">
                            <h3>Template</h3>
                            <input type="number" className="center" placeholder="Total E-Mails to be sent" onChange={this.setPointsToUse}></input>
                            <textarea type="text" placeholder="Enter your Custom HTML or your Message" onChange={this.setMsgToSend}></textarea>
                            <button onClick={this.setDecisorOn}>Send!</button>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
