export default class ProductsSlider extends React.Component {

    render(){

        function returnProducts(products){
            let productsArray = []

            products.forEach( el => {
                productsArray.push(<Product title={el.title} description={el.description} imgUrl={el.imgUrl}></Product>)
            });

            return productsArray;
        }

        const products = returnProducts(this.props.products);

        const responsive = {
            superLargeDesktop: {
              // the naming can be any, depends on you.
              breakpoint: { max: 4000, min: 3000 },
              items: 5,
            },
            desktop: {
              breakpoint: { max: 3000, min: 1024 },
              items: 3,
            },
            tablet: {
              breakpoint: { max: 1024, min: 464 },
              items: 2,
            },
            mobile: {
              breakpoint: { max: 464, min: 0 },
              items: 1,
            },
          };
        return(
            <div className="products-slider col-md-12">
                <Carousel responsive={responsive} swipeable={true} ssr={true} keyBoardControl={false}>
                                {this.props.products}
                </Carousel>
            </div>
        )
    }
}
