const MongoClient = require('mongodb').MongoClient;
const uri = "mongodb+srv://main-user:09730Success@mailmarketme-k4esh.mongodb.net/test?retryWrites=true&w=majority";

const client = new MongoClient(uri, {useNewUrlParser: true});

let accounts;
client.connect(err => {
  if (err) throw(err);
  const db = client.db("MailMarketMe");
    db.collection("accounts").find({}, (err, result) => {
      if (err) throw err;
        accounts = result;
    });
});

export default (req, res) => {
  if(req.method === "POST" && req.body.username != undefined && req.body.username != null && req.body.password != undefined && req.body.password != null){
    res.send(accounts);
  }
}
