import Carousel from "react-multi-carousel";
import Product from "./Product";

export default class Products extends React.Component{


    render(){

        function returnProducts(props){
            let productsArray = []

            props.products.forEach( el => {
                productsArray.push(<Product title={el.title} description={el.description} imgUrl={el.imgUrl}></Product>)
            });

            return productsArray;
        }

        const promoProducts = returnProducts(this.props);

        const responsive = {
            superLargeDesktop: {
              // the naming can be any, depends on you.
              breakpoint: { max: 4000, min: 3000 },
              items: 5,
            },
            desktop: {
              breakpoint: { max: 3000, min: 1024 },
              items: 3,
            },
            tablet: {
              breakpoint: { max: 1024, min: 464 },
              items: 2,
            },
            mobile: {
              breakpoint: { max: 464, min: 0 },
              items: 1,
            },
          };

        return(
            <React.Fragment>
                <div className="products container">
                    <div className="row">
                        <div className="products-categories col-md-12">
                            <Carousel responsive={responsive} swipeable={true} ssr={true} keyBoardControl={false}>
                                {promoProducts}
                            </Carousel>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}
