const mongodb = require("mongodb");
const MongoClient = mongodb.MongoClient;
const uri = "mongodb+srv://main-user:09730Success@mailmarketme-k4esh.mongodb.net/test?retryWrites=true&w=majority";

const client = new MongoClient(uri, {useNewUrlParser: true, useUnifiedTopology: true});

export default (req, res) => {
  if(req.method === "POST" && req.body.id != undefined && req.body.id != null){
    client.connect(err => {
      if(err) throw err;
      const db = client.db("MailMarketMe");
        db.collection("accounts").findOne({_id: mongodb.ObjectId("" + req.body.id)}, (err, result) => {
            if(err) throw err;
            if(result != null){
              res.status(200).end(result);
            }else {
              res.status(406).end();
            }
        });
    });
  }
  else{
    res.status(406).end();
  }
}
