import Head from "next/head";
import checkAuth from "../lib/checkAuth";
import axios from "axios";
export default class DashBoardIndex extends React.Component {

    state = {
        sent: 0,
        points: 0,
        pointsToUse: 0,
        leads: 0,
        decisor: false,
        loading: true,
    }

    static async getInitialProps({req, res}){
        return checkAuth(req, res);
    }

    componentDidMount(){
        if(this.props.loggedIn && this.props.id != null){
            axios({
                url: "/api/user",
                method: "POST",
                data: {
                    id: this.props.id
                }
            }).then(response => {
                this.setState( {
                    sent: response.data.sent,
                    points: response.data.points,
                    leads: response.data.leads
                });
            }).catch(err =>{
                if (err) console.log(err);
            })
        }
    }

    render(){

        function decisorDom(points, pointsToBeUsed){
            if(pointsToBeUsed <= points){
                return(
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                                <h2>You will use {pointsToBeUsed} points.<br></br>Are you sure?</h2>
                            </div>
                            <div className="col-md-12 row">
                                <div className="col-md-6">
                                    <button className="decisor-button white">Yes</button>
                                </div>
                                <div className="col-md-6">
                                    <button className="decisor-button black">Yes</button>
                                </div>
                            </div>
                        </div>
                    </div>
                );
            }
        }


        return(
            <React.Fragment>
                <Head>
                    <title>Dashboard</title>
                    <link href="/css/bootstrap.css" rel="stylesheet"></link>
                    <link href="/css/dashboard.css" rel="stylesheet"></link>
                </Head>
                <div className="container-fluid">
                    <div className={this.state.decisor ? "decisor active" : "decisor"}>
                    </div>
                    <div className="row center">
                        <div className="col-md-4">
                            <div className="statistics-block">
                                <h2>E-Mails Sent</h2>
                                <h3>{this.state.sent}</h3>
                            </div>
                        </div>
                        <div className="col-md-4">
                        <div className="statistics-block">
                                <h2>Available Points</h2>
                                <h3>{this.state.points}</h3>
                            </div>
                        </div>
                        <div className="col-md-4">
                        <div className="statistics-block">
                                <h2>Leads</h2>
                                <h3>{this.state.leads}</h3>
                            </div>
                        </div>
                    </div>
                    <div className="row" style={{margin: "5rem 0"}}>
                        <div className="col-md-4 input-block center">
                            <h3>Custom E-Mail List (Seperate by comma)</h3>
                            <textarea className="custom-list" type="text" placeholder="Leave blank if you don't want to add custom E-Mails"></textarea>
                        </div>
                        <div className="col-md-8 input-block center">
                            <h3>Template</h3>
                            <input type="number" className="center" placeholder="Total E-Mails to be sent"></input>
                            <textarea type="text" placeholder="Enter your Custom HTML or your Message"></textarea>
                            <button>Send!</button>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
