MongoClient.connect(uri, function(err, db) {
    if (err) throw err;
    var dbo = db.db("MailMarketMe");
    dbo.collection("accounts").findOne({}, function(err, result) {
      if (err) throw err;
      console.log(result.email);
      db.close();
