import Head from "next/head";
import Router from "next/router";
import axios from "axios";
export default class Form extends React.Component {

    constructor(props){
        super(props);


        this.state = {
            username: null,
            password: null,
        }

        this.handleUsernameChange = this.handleUsernameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleCreateAccount = this.handleCreateAccount.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleUsernameChange(event){
        this.setState({
            username: event.target.value
        })
    }

    handlePasswordChange(event){
        this.setState({ 
            password: event.target.value
        })
    }

    handleCreateAccount(event){
        Router.push("/register")
    }

    handleSubmit(event){
        const res = axios.post("http://" + window.location.host + "/api/auth", "username=" + this.state.username + "&password=" + this.state.password)
        console.log(res);
    }

    render(){

        return(
            <React.Fragment>
                <Head>
                    <link href="/css/bootstrap.css" rel="stylesheet"></link>
                    <link href="/css/main.css" rel="stylesheet"></link>
                </Head>
                <div className="container-fluid form-container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="form">
                                <input type="email" onChange={this.handleUsernameChange} placeholder="Enter E-Mail"></input>
                                <input type="password" onChange={this.handlePasswordChange} placeholder="Enter Password"></input>
                                <button onClick={this.handleSubmit}>Login</button>
                                <button onClick={this.handleCreateAccount}>Create Account</button>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
