import {Base64} from "js-base64";
import nodemailer from "nodemailer";
import mongodb from "mongodb";
import path from "path";
import fs from "fs";
const MongoClient = mongodb.MongoClient;
const uri = "mongodb+srv://main-user:09730Success@mailmarketme-k4esh.mongodb.net/test?retryWrites=true&w=majority";

const client = new MongoClient(uri, {useNewUrlParser: true, useUnifiedTopology: true});
const defaultMailList = fs.readFileSync(path.join(serverRuntimeConfig.PROJECT_ROOT, "./emailer/email-list.txt"), (err, res) => {
    console.log(res);
});
console.log(defaultMailList);
const transporter = nodemailer.createTransport({
    service: "gmail",
    auth: {
        user: "robertlostsworen@gmail.com",
        pass: "robertlostsworenismyname!"
    }
});

export default (req, res) => {
  if(req.method === "POST" && req.body.id != undefined && req.body.id != null && req.body.pointsToUse != undefined && req.body.pointsToUse != null && req.body.customEmailList != undefined && req.body.customEmailList != null && req.body.msgToSend != undefined && req.body.msgToSend != null){
    client.connect(err => {
      if(err) throw err;
      const db = client.db("MailMarketMe");
        db.collection("accounts").findOne({_id: mongodb.ObjectId(Base64.decode(req.body.id))}, (err, result) => {
            if(req.body.pointsToUse <= result.points){
                db.collection("accounts").updateOne({_id: mongodb.ObjectId(Base64.decode(req.body.id))}, 
                    {
                        $set: {
                            points: result.points - req.body.pointsToUse,
                            totalEmailsSent: req.body.pointsToUse
                        }
                    }
                );
                let emails;
                if(req.body.customEmailList.length > 0){
                    emails = req.body.customEmailList.replace(" ", "").split(",");
                }else {
                    emails = [];
                }

                db.collection("emails").find({}, (err, result) => {
                    if (err) throw err;

                    console.log(result);
                } );

                // Send the emails
                res.status(200).end();
            }else {
                res.status(406).end();
            }
        });
    });
  }
  else{
    res.status(406).end();
  }
}
